# Create contract instance
ens_registry_contract = web3.eth.contract(address=ens_name_wrapper_address, abi=ens_name_wrapper_abi)

# Parent domain and subdomain details
parent_domain = "txgpt.eth"
subdomain_label = "6930658904"

# Calculate the label hashes

def namehash(name):
    """Generates the ENS namehash for a given domain."""
    node = b'\x00' * 32
    if name:
        labels = name.split('.')
        for label in reversed(labels):
            labelhash = keccak(text=label)
            node = keccak(node + labelhash)
    return HexBytes(node)

parent_node = namehash(parent_domain)
resolver_address="0x8FADE66B79cC9f707aB26799354482EB93a5B7dD"
ttl = 0
fuses = 0
expiry = 0

# Create the subdomain
def create_subdomain():
    transaction = ens_registry_contract.functions.setSubnodeRecord(
        parent_node.hex(), # parent node (namehash of parent domain)
        subdomain_label,  # label of the subdomain
        account.address,  # owner address of the new subdomain
        resolver_address,  # resolver address
        ttl,  # time-to-live
        fuses,  # fuses
        expiry  # expiry
    ).build_transaction({
        'chainId': 11155111,  # Sepolia testnet chain ID
        'gas': 2000000,
        'gasPrice': web3.to_wei('10', 'gwei'),
        'nonce': web3.eth.get_transaction_count(account.address),
    })

    # Sign the transaction
    signed_txn = web3.eth.account.sign_transaction(transaction, private_key=private_key)

    # Send the transaction
    tx_hash = web3.eth.send_raw_transaction(signed_txn.rawTransaction)

    # Wait for the transaction to be mined
    tx_receipt = web3.eth.wait_for_transaction_receipt(tx_hash)
    return tx_receipt

# Create the subdomain and get the transaction receipt
receipt = create_subdomain()
print(f"Transaction receipt: {receipt}")
